import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Image, Button, Pressable, Alert, TextInput, AppRegistry } from 'react-native';
import axios from 'axios';

const RegistrationScreen = () =>{

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const [isSubmit, setIsSubmit] = useState(false);

    useEffect(() => {
        const authenticate = async() =>{
            axios.post(
                "https://nuzul.com.my/login.php",
                JSON.stringify({
                    username: username,
                    password: password
                })
            ).then((response) => {
                console.log(response.data);
                alert(response.data.result);
                setIsSubmit(false);
                //navigate users based on the response
            }).catch((err) =>{
                console.log(err);
            }
            )
        };
        if (isSubmit) authenticate();
    }, [isSubmit]);

    const usernameHandler = (text) =>{
        //validations
        setUsername(text);
    }

    return (
        <View style={styles.container}>
            {/* <Text style={styles.colorr}>UCYP</Text> */}
            <Image
        style={styles.logo}
        source={{
          uri: 'https://www.ucyp.edu.my/wp-content/uploads/2018/06/xlogo-UCYP-02-3.png.pagespeed.ic_.xhDmre2uYj.png',
        }}
      />
            <TextInput
             placeholder="Username" 
             autoCapitalize="none"
             style={styles.inputStyle} 
             onChangeText={usernameHandler}/>
            <TextInput 
            placeholder="Password" 
            autoCapitalize="none"
            style={styles.inputStyle}
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)} />
            <Pressable style={styles.button} onPress={() => setIsSubmit(true) }>
                <Text style={styles.colorr}>Log In</Text>
            </Pressable>
            {/* <Button 
            style={styles.buttonStyle}
            title="Log In" onPress={() => setIsSubmit(true) }/> */}
        </View>
    )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
      height: 104,
      width: 274
  },
  inputStyle: {
    width: "80%",
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    backgroundColor: "#fff",
    borderBottomColor: "#fff",
    borderTopColor: "#fff",
    borderLeftColor: "#fff",
    borderRightColor: "#fff",
    borderBottomWidth: 1,
    borderTopWidth: 1,
    borderLeftWidth: 1,
    borderRightWidth: 1,
    borderRadius: 5,
    marginBottom: 5,
  },
  buttonStyle: {
      width: "70%",
      height: 40
  },
  colorr: {
    color: "white",
    fontSize: 23
  }
});

export default RegistrationScreen;
