import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Image, Button, Pressable, Alert, TextInput, AppRegistry } from 'react-native';
import axios from 'axios';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { SafeAreaView } from 'react-native-safe-area-context';

const Stack = createNativeStackNavigator();

const RegistrationScreen2 = () =>{
    return(
        <NavigationContainer>
            <Stack.Navigator>
                <Stack.Screen component={RegistrationScreen} name="RegistrationScreen" options={{headerShown: false}} />
                <Stack.Screen component={AttendanceFunction} name="Atttendance" />
            </Stack.Navigator>
        </NavigationContainer>
    );
}

const RegistrationScreen = ({navigation}) =>{
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const [isSubmit, setIsSubmit] = useState(false);

    useEffect(() => {
        const authenticate = async() =>{
            axios.post(
                "https://staff.ucyp.edu.my/STAFF_UCYP/login/login_reactnative.php",
                JSON.stringify({
                    username: username,
                    password: password
                })
            ).then((response) => {
                
                setIsSubmit(false);
                //navigate users based on the response
                if(response.data.result == 'logged in')
                {
                    navigation.navigate('Atttendance', {nama: response.data.nama, id: response.data.idinfostaf});
                    //alert(response.data.nama);
                }
                else
                {
                    // console.log(response.data);
                    alert(response.data.result);
                }
            }).catch((err) =>{
                console.log(err);
            }
            )
        };
        if (isSubmit) authenticate();
    }, [isSubmit]);

    const usernameHandler = (text) =>{
        //validations
        setUsername(text);
    }

    return (
        <View style={styles.container}>
            {/* <Text style={styles.colorr}>UCYP</Text> */}
            <Image
                style={styles.logo}
                source={{
                uri: 'https://www.ucyp.edu.my/wp-content/uploads/2018/06/xlogo-UCYP-02-3.png.pagespeed.ic_.xhDmre2uYj.png',
                }}
            />
            <TextInput
             placeholder="Username" 
             autoCapitalize="none"
             style={styles.inputStyle} 
             onChangeText={usernameHandler}/>
            <TextInput 
            placeholder="Password" 
            autoCapitalize="none"
            style={styles.inputStyle}
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)} />
            {/* <Pressable style={styles.buttonStyle} onPress={() => setIsSubmit(true) }>
                <Text style={styles.colorr}>Log In</Text>
            </Pressable> */}
            <View style={{margin:10, width: "35%", fontSize: 50}}>
                <Button
                color="#FB695A"
                style={styles.buttonStyle}
                title="Log In" onPress={() => setIsSubmit(true) }/>
            </View>
        </View>
    )
}

const AttendanceFunction = ({route, navigation}) =>{
    const { nama } = route.params;
    const { idinfostaf } = route.params;

    //Hook
    const [checkclockin, setCheckclockin] = useState(true);
    const [butangTitle, setButangTitle] = useState("...checking");

    useEffect(() => {
        const authenticate3 = async() =>{
            axios.post(
                "https://staff.ucyp.edu.my/STAFF_UCYP/login/staffattendancecheck_reactnative.php",
                JSON.stringify({
                    id: route.params.id
                })
            ).then((response) => {
                
                setCheckclockin(false);
                //navigate users based on the response
                alert(response.data.statusCode);
                if(response.data.statusCode == 1)
                {
                    //alert(response.data.idinfostaf);
                    setButangTitle("CLOCK OUT");

                }
                else
                {
                    setButangTitle("CLOCK IN");
                }
            }).catch((err) =>{
                console.log(err);
            }
            )
        };
        if (checkclockin) authenticate3();
    }, [checkclockin]);

    const [isClockedin, setIsisClockedin] = useState(false);

    useEffect(() => {
        const authenticate2 = async() =>{
            axios.post(
                "https://staff.ucyp.edu.my/STAFF_UCYP/login/staffattendance_reactnative.php",
                JSON.stringify({
                    id: route.params.id
                })
            ).then((response) => {
                
                setIsisClockedin(false);
                //navigate users based on the response
                if(response.data.statusCode == 1)
                {
                    alert(response.data.statusCode);
                }
                else
                {
                    alert("API error. Please Contact IT Admin");
                }
            }).catch((err) =>{
                console.log(err);
            }
            )
        };
        if (isClockedin) authenticate2();
    }, [isClockedin]);

    const [dt, setDt] = useState(new Date().toLocaleString());

    useEffect(() => {
        let secTimer = setInterval( () => {
        setDt(new Date().toLocaleString())
        },1000)

        return () => clearInterval(secTimer);
    }, []);

    return(
        <SafeAreaView style={styles.container}>
            <Image
                style={styles.logo}
                source={{
                uri: 'https://www.ucyp.edu.my/wp-content/uploads/2018/06/xlogo-UCYP-02-3.png.pagespeed.ic_.xhDmre2uYj.png',
                }}
            />
            <View>
                <Text style={{color: "white", fontSize: 23, textAlign: "center"}}>
                    {nama} {"\n"}
                </Text>
                <Text style={styles.colorr}>
                    {dt}
                </Text>
            </View>
            <View style={{margin:10, width: "35%", fontSize: 50}}>
                <Button
                style={styles.buttonStyle}
                title={butangTitle} onPress={() => setIsisClockedin(true) }/>
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
      height: 104,
      width: 274
  },
  inputStyle: {
    width: "80%",
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    backgroundColor: "#fff",
    borderBottomColor: "#fff",
    borderTopColor: "#fff",
    borderLeftColor: "#fff",
    borderRightColor: "#fff",
    borderBottomWidth: 1,
    borderTopWidth: 1,
    borderLeftWidth: 1,
    borderRightWidth: 1,
    borderRadius: 5,
    marginBottom: 5,
  },
  buttonStyle: {
      height: 40,
      width: "70%",
      backgroundColor: "green",
      borderRadius: 10,
  },
  colorr: {
    color: "white",
    fontSize: 23,
    textAlign: "center"
  }
});

export default RegistrationScreen2;
