import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Image, Button, Pressable, Alert, TextInput, AppRegistry } from 'react-native';
import axios from 'axios';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Stack = createNativeStackNavigator();

const RegistrationScreen2 = () =>{
    return(
        <NavigationContainer>
            <Stack.Navigator>
                <Stack.Screen component={RegistrationScreen} name="RegistrationScreen" options={{headerShown: false}} />
                <Stack.Screen component={Attendance} name="Att" />
            </Stack.Navigator>
        </NavigationContainer>
    );
}

const RegistrationScreen = ({navigation}) =>{

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const [isSubmit, setIsSubmit] = useState(false);

    useEffect(() => {
        const authenticate = async() =>{
            axios.post(
                "https://nuzul.com.my/login.php",
                JSON.stringify({
                    username: username,
                    password: password
                })
            ).then((response) => {
                
                setIsSubmit(false);
                //navigate users based on the response
                if(response.data.result == 'logged in')
                {
                    navigation.navigate('Att');
                }
                else
                {
                    // console.log(response.data);
                    alert(response.data.result);
                }
            }).catch((err) =>{
                console.log(err);
            }
            )
        };
        if (isSubmit) authenticate();
    }, [isSubmit]);

    const usernameHandler = (text) =>{
        //validations
        setUsername(text);
    }

    return (
        <View style={styles.container}>
            {/* <Text style={styles.colorr}>UCYP</Text> */}
            <Image
        style={styles.logo}
        source={{
          uri: 'https://www.ucyp.edu.my/wp-content/uploads/2018/06/xlogo-UCYP-02-3.png.pagespeed.ic_.xhDmre2uYj.png',
        }}
      />
            <TextInput
             placeholder="Username" 
             autoCapitalize="none"
             style={styles.inputStyle} 
             onChangeText={usernameHandler}/>
            <TextInput 
            placeholder="Password" 
            autoCapitalize="none"
            style={styles.inputStyle}
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)} />
            {/* <Pressable style={styles.buttonStyle} onPress={() => setIsSubmit(true) }>
                <Text style={styles.colorr}>Log In</Text>
            </Pressable> */}
            <View style={{margin:10, width: "35%", fontSize: 50}}>
                <Button
                color="#FB695A"
                style={styles.buttonStyle}
                title="Log In" onPress={() => setIsSubmit(true) }/>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
      height: 104,
      width: 274
  },
  inputStyle: {
    width: "80%",
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    backgroundColor: "#fff",
    borderBottomColor: "#fff",
    borderTopColor: "#fff",
    borderLeftColor: "#fff",
    borderRightColor: "#fff",
    borderBottomWidth: 1,
    borderTopWidth: 1,
    borderLeftWidth: 1,
    borderRightWidth: 1,
    borderRadius: 5,
    marginBottom: 5,
  },
  buttonStyle: {
      height: 40,
      width: "70%",
      backgroundColor: "blue"
  },
  colorr: {
    color: "white",
    fontSize: 23
  }
});

const Attendance = ({testt}) =>{
    return(
        <View style={styles.container}>
            <Text style={styles.colorr}>
                {testt}
            </Text>
        </View>
    );
}

export default RegistrationScreen2;
